// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity =0.7.6;
pragma abicoder v2;

import '@openzeppelin/contracts-upgradeable/proxy/Initializable.sol';
import '@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol';

import './interfaces/INonfungibleTokenPositionDescriptor.sol';

/// @title Describes NFT token positions
contract NonfungibleTokenPositionDescriptorOffChain is INonfungibleTokenPositionDescriptor, Initializable {
    using StringsUpgradeable for uint256;

    string private _baseTokenURI;

    function initialize(string calldata baseTokenURI) external initializer {
        _baseTokenURI = baseTokenURI;
    }

    /// @inheritdoc INonfungibleTokenPositionDescriptor
    function tokenURI(INonfungiblePositionManager positionManager, uint256 tokenId)
        external
        view
        override
        returns (string memory)
    {
        return bytes(_baseTokenURI).length > 0 ? string(abi.encodePacked(_baseTokenURI, tokenId.toString())) : "";
    }
}

// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity =0.7.6;
pragma abicoder v2;

import '@openzeppelin/contracts-upgradeable/proxy/Initializable.sol';
import '@openzeppelin/contracts-upgradeable/utils/StringsUpgradeable.sol';

import './interfaces/INonfungibleTokenPositionDescriptor.sol';

/// @title Describes NFT token positions
contract NonfungibleTokenPositionDescriptorOffChainV2 is INonfungibleTokenPositionDescriptor, Initializable {
    using StringsUpgradeable for uint256;

    string private _baseTokenURI;

    /**
     * @dev Indicates that the contract has been initialized.
     */
    bool private _initializedV2;

    /**
     * @dev Modifier to protect an initializer function from being invoked twice.
     */
    modifier initializerV2() {
        require(!_initializedV2, "Already initialized V2");

        _initializedV2 = true;

        _;
    }

    function initialize(string calldata baseTokenURI) external initializer {
        _baseTokenURI = baseTokenURI;
    }

    function initializeV2(string calldata baseTokenURI) external initializerV2 {
        _baseTokenURI = baseTokenURI;
    }

    /// @inheritdoc INonfungibleTokenPositionDescriptor
    function tokenURI(INonfungiblePositionManager positionManager, uint256 tokenId)
        external
        view
        override
        returns (string memory)
    {
        return bytes(_baseTokenURI).length > 0 ? string(abi.encodePacked(_baseTokenURI, tokenId.toString())) : "";
    }
}

contracts/SwapRouter.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity =0.7.6;
pragma abicoder v2;

import '@pancakeswap/v3-core/contracts/libraries/SafeCast.sol';
import '@pancakeswap/v3-core/contracts/libraries/TickMath.sol';
import '@pancakeswap/v3-core/contracts/interfaces/IBubblySwapPool.sol';

import './interfaces/ISwapRouter.sol';
import './base/PeripheryImmutableState.sol';
import './base/PeripheryValidation.sol';
import './base/PeripheryPaymentsWithFee.sol';
import './base/Multicall.sol';
import './base/SelfPermit.sol';
import './libraries/Path.sol';
import './libraries/PoolAddress.sol';
import './libraries/CallbackValidation.sol';
import './interfaces/external/IWETH9.sol';

/// @title Pancake V3 Swap Router
/// @notice Router for stateless execution of swaps against Pancake V3
contract SwapRouter is
    ISwapRouter,
    PeripheryImmutableState,
    PeripheryValidation,
    PeripheryPaymentsWithFee,
    Multicall,
    SelfPermit
{
    using Path for bytes;
    using SafeCast for uint256;

    /// @dev Used as the placeholder value for amountInCached, because the computed amount in for an exact output swap
    /// can never actually be this value
    uint256 private constant DEFAULT_AMOUNT_IN_CACHED = type(uint256).max;

    /// @dev Transient storage variable used for returning the computed amount in for an exact output swap.
    uint256 private amountInCached = DEFAULT_AMOUNT_IN_CACHED;

    constructor(address _deployer, address _factory, address _WETH9) PeripheryImmutableState(_deployer, _factory, _WETH9) {}

    /// @dev Returns the pool for the given token pair and fee. The pool contract may or may not exist.
    function getPool(
        address tokenA,
        address tokenB,
        uint24 fee
    ) private view returns (IBubblySwapPool) {
        return IBubblySwapPool(PoolAddress.computeAddress(deployer, PoolAddress.getPoolKey(tokenA, tokenB, fee)));
    }

    struct SwapCallbackData {
        bytes path;
        address payer;
    }

    /// @inheritdoc IBubblySwapSwapCallback
    function BubblySwapSwapCallback(
        int256 amount0Delta,
        int256 amount1Delta,
        bytes calldata _data
    ) external override {
        require(amount0Delta > 0 || amount1Delta > 0); // swaps entirely within 0-liquidity regions are not supported
        SwapCallbackData memory data = abi.decode(_data, (SwapCallbackData));
        (address tokenIn, address tokenOut, uint24 fee) = data.path.decodeFirstPool();
        CallbackValidation.verifyCallback(deployer, tokenIn, tokenOut, fee);

        (bool isExactInput, uint256 amountToPay) =
            amount0Delta > 0
                ? (tokenIn < tokenOut, uint256(amount0Delta))
                : (tokenOut < tokenIn, uint256(amount1Delta));
        if (isExactInput) {
            pay(tokenIn, data.payer, msg.sender, amountToPay);
        } else {
            // either initiate the next swap or pay
            if (data.path.hasMultiplePools()) {
                data.path = data.path.skipToken();
                exactOutputInternal(amountToPay, msg.sender, 0, data);
            } else {
                amountInCached = amountToPay;
                tokenIn = tokenOut; // swap in/out because exact output swaps are reversed
                pay(tokenIn, data.payer, msg.sender, amountToPay);
            }
        }
    }

    /// @dev Performs a single exact input swap
    function exactInputInternal(
        uint256 amountIn,
        address recipient,
        uint160 sqrtPriceLimitX96,
        SwapCallbackData memory data
    ) private returns (uint256 amountOut) {
        // allow swapping to the router address with address 0
        if (recipient == address(0)) recipient = address(this);

        (address tokenIn, address tokenOut, uint24 fee) = data.path.decodeFirstPool();

        bool zeroForOne = tokenIn < tokenOut;

        (int256 amount0, int256 amount1) =
            getPool(tokenIn, tokenOut, fee).swap(
                recipient,
                zeroForOne,
                amountIn.toInt256(),
                sqrtPriceLimitX96 == 0
                    ? (zeroForOne ? TickMath.MIN_SQRT_RATIO + 1 : TickMath.MAX_SQRT_RATIO - 1)
                    : sqrtPriceLimitX96,
                abi.encode(data)
            );

        return uint256(-(zeroForOne ? amount1 : amount0));
    }

    /// @inheritdoc ISwapRouter
    function exactInputSingle(ExactInputSingleParams calldata params)
        external
        payable
        override
        checkDeadline(params.deadline)
        returns (uint256 amountOut)
    {
        amountOut = exactInputInternal(
            params.amountIn,
            params.recipient,
            params.sqrtPriceLimitX96,
            SwapCallbackData({path: abi.encodePacked(params.tokenIn, params.fee, params.tokenOut), payer: msg.sender})
        );
        require(amountOut >= params.amountOutMinimum, 'Too little received');
    }

    /// @inheritdoc ISwapRouter
    function exactInput(ExactInputParams memory params)
        external
        payable
        override
        checkDeadline(params.deadline)
        returns (uint256 amountOut)
    {
        address payer = msg.sender; // msg.sender pays for the first hop

        while (true) {
            bool hasMultiplePools = params.path.hasMultiplePools();

            // the outputs of prior swaps become the inputs to subsequent ones
            params.amountIn = exactInputInternal(
                params.amountIn,
                hasMultiplePools ? address(this) : params.recipient, // for intermediate swaps, this contract custodies
                0,
                SwapCallbackData({
                    path: params.path.getFirstPool(), // only the first pool in the path is necessary
                    payer: payer
                })
            );

            // decide whether to continue or terminate
            if (hasMultiplePools) {
                payer = address(this); // at this point, the caller has paid
                params.path = params.path.skipToken();
            } else {
                amountOut = params.amountIn;
                break;
            }
        }

        require(amountOut >= params.amountOutMinimum, 'Too little received');
    }

    /// @dev Performs a single exact output swap
    function exactOutputInternal(
        uint256 amountOut,
        address recipient,
        uint160 sqrtPriceLimitX96,
        SwapCallbackData memory data
    ) private returns (uint256 amountIn) {
        // allow swapping to the router address with address 0
        if (recipient == address(0)) recipient = address(this);

        (address tokenOut, address tokenIn, uint24 fee) = data.path.decodeFirstPool();

        bool zeroForOne = tokenIn < tokenOut;

        (int256 amount0Delta, int256 amount1Delta) =
            getPool(tokenIn, tokenOut, fee).swap(
                recipient,
                zeroForOne,
                -amountOut.toInt256(),
                sqrtPriceLimitX96 == 0
                    ? (zeroForOne ? TickMath.MIN_SQRT_RATIO + 1 : TickMath.MAX_SQRT_RATIO - 1)
                    : sqrtPriceLimitX96,
                abi.encode(data)
            );

        uint256 amountOutReceived;
        (amountIn, amountOutReceived) = zeroForOne
            ? (uint256(amount0Delta), uint256(-amount1Delta))
            : (uint256(amount1Delta), uint256(-amount0Delta));
        // it's technically possible to not receive the full output amount,
        // so if no price limit has been specified, require this possibility away
        if (sqrtPriceLimitX96 == 0) require(amountOutReceived == amountOut);
    }

    /// @inheritdoc ISwapRouter
    function exactOutputSingle(ExactOutputSingleParams calldata params)
        external
        payable
        override
        checkDeadline(params.deadline)
        returns (uint256 amountIn)
    {
        // avoid an SLOAD by using the swap return data
        amountIn = exactOutputInternal(
            params.amountOut,
            params.recipient,
            params.sqrtPriceLimitX96,
            SwapCallbackData({path: abi.encodePacked(params.tokenOut, params.fee, params.tokenIn), payer: msg.sender})
        );

        require(amountIn <= params.amountInMaximum, 'Too much requested');
        // has to be reset even though we don't use it in the single hop case
        amountInCached = DEFAULT_AMOUNT_IN_CACHED;
    }

    /// @inheritdoc ISwapRouter
    function exactOutput(ExactOutputParams calldata params)
        external
        payable
        override
        checkDeadline(params.deadline)
        returns (uint256 amountIn)
    {
        // it's okay that the payer is fixed to msg.sender here, as they're only paying for the "final" exact output
        // swap, which happens first, and subsequent swaps are paid for within nested callback frames
        exactOutputInternal(
            params.amountOut,
            params.recipient,
            0,
            SwapCallbackData({path: params.path, payer: msg.sender})
        );

        amountIn = amountInCached;
        require(amountIn <= params.amountInMaximum, 'Too much requested');
        amountInCached = DEFAULT_AMOUNT_IN_CACHED;
    }
}

contracts/V3Migrator.sol.content:
// SPDX-License-Identifier: GPL-2.0-or-later
pragma solidity =0.7.6;
pragma abicoder v2;

import '@pancakeswap/v3-core/contracts/libraries/LowGasSafeMath.sol';
import '@uniswap/v2-core/contracts/interfaces/IUniswapV2Pair.sol';

import './interfaces/INonfungiblePositionManager.sol';

import './libraries/TransferHelper.sol';

import './interfaces/IV3Migrator.sol';
import './base/PeripheryImmutableState.sol';
import './base/Multicall.sol';
import './base/SelfPermit.sol';
import './interfaces/external/IWETH9.sol';
import './base/PoolInitializer.sol';

/// @title Pancakeswap Migrator
contract V3Migrator is IV3Migrator, PeripheryImmutableState, PoolInitializer, Multicall, SelfPermit {
    using LowGasSafeMath for uint256;

    address public immutable nonfungiblePositionManager;

    constructor(
        address _deployer,
        address _factory,
        address _WETH9,
        address _nonfungiblePositionManager
    ) PeripheryImmutableState(_deployer, _factory, _WETH9) {
        nonfungiblePositionManager = _nonfungiblePositionManager;
    }

    receive() external payable {
        require(msg.sender == WETH9, 'Not WETH9');
    }

    function migrate(MigrateParams calldata params) external override {
        require(params.percentageToMigrate > 0, 'Percentage too small');
        require(params.percentageToMigrate <= 100, 'Percentage too large');

        // burn v2 liquidity to this address
        IUniswapV2Pair(params.pair).transferFrom(msg.sender, params.pair, params.liquidityToMigrate);
        (uint256 amount0V2, uint256 amount1V2) = IUniswapV2Pair(params.pair).burn(address(this));

        // calculate the amounts to migrate to v3
        uint256 amount0V2ToMigrate = amount0V2.mul(params.percentageToMigrate) / 100;
        uint256 amount1V2ToMigrate = amount1V2.mul(params.percentageToMigrate) / 100;

        // approve the position manager up to the maximum token amounts
        TransferHelper.safeApprove(params.token0, nonfungiblePositionManager, amount0V2ToMigrate);
        TransferHelper.safeApprove(params.token1, nonfungiblePositionManager, amount1V2ToMigrate);

        // mint v3 position
        (, , uint256 amount0V3, uint256 amount1V3) =
            INonfungiblePositionManager(nonfungiblePositionManager).mint(
                INonfungiblePositionManager.MintParams({
                    token0: params.token0,
                    token1: params.token1,
                    fee: params.fee,
                    tickLower: params.tickLower,
                    tickUpper: params.tickUpper,
                    amount0Desired: amount0V2ToMigrate,
                    amount1Desired: amount1V2ToMigrate,
                    amount0Min: params.amount0Min,
                    amount1Min: params.amount1Min,
                    recipient: params.recipient,
                    deadline: params.deadline
                })
            );

        // if necessary, clear allowance and refund dust
        if (amount0V3 < amount0V2) {
            if (amount0V3 < amount0V2ToMigrate) {
                TransferHelper.safeApprove(params.token0, nonfungiblePositionManager, 0);
            }

            uint256 refund0 = amount0V2 - amount0V3;
            if (params.refundAsETH && params.token0 == WETH9) {
                IWETH9(WETH9).withdraw(refund0);
                TransferHelper.safeTransferETH(msg.sender, refund0);
            } else {
                TransferHelper.safeTransfer(params.token0, msg.sender, refund0);
            }
        }
        if (amount1V3 < amount1V2) {
            if (amount1V3 < amount1V2ToMigrate) {
                TransferHelper.safeApprove(params.token1, nonfungiblePositionManager, 0);
            }

            uint256 refund1 = amount1V2 - amount1V3;
            if (params.refundAsETH && params.token1 == WETH9) {
                IWETH9(WETH9).withdraw(refund1);
                TransferHelper.safeTransferETH(msg.sender, refund1);
            } else {
                TransferHelper.safeTransfer(params.token1, msg.sender, refund1);
            }
        }
    }
}

contracts/vault.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;
pragma abicoder v2;
import './interfaces/INonfungiblePositionManager.sol';
import './base/PeripheryValidation.sol';
import { ERC1155Receiver } from "@openzeppelin/contracts/token/ERC1155/ERC1155Receiver.sol";
// 引入USDC合约接口
interface IERC20 {
    function transfer(address recipient, uint256 amount) external returns (bool);
    function transferFrom(address sender, address recipient, uint256 amount) external returns (bool);
    function balanceOf(address account) external view returns (uint256);
}
interface CTF {
    function splitPosition(
        IERC20 collateralToken,
        bytes32 parentCollectionId,
        bytes32 conditionId,
        uint[] calldata partition,
        uint amount
    ) external;
}
contract Vault is PeripheryValidation,ERC1155Receiver{

    // 固定日利率，比如0.1%每日利率
    uint256 public constant DAILY_INTEREST_RATE = 1; // 每日利率1，即0.1%（1e-4）

    uint256 public constant SECONDS_IN_A_DAY = 1 days; // 每天的秒数
    uint256 public constant FIXED_POINT = 1e18; // 固定点常量，用于精确计算

    // USDC token地址
    address public NonfungiblePositionManager;
    address public usdcTokenAddress;
    address public ctfAddress;
    IERC20 public usdc;

    // 存款信息
    struct Deposit {
        uint256 principal; // 本金
        uint256 interestAccrued; // 累计利息
        uint256 lastDepositTime; // 上次存入的时间
    }

    mapping(address => Deposit) public deposits;

    // 总本金和总利息
    uint256 public totalPrincipal;
    uint256 public totalInterest;

    // 合约创建时间（首次存款时间）
    uint256 public vaultCreationTime;

    // 上次更新时间戳
    uint256 public lastUpdateTimestamp;

    constructor(address _usdcTokenAddress, address _NonfungiblePositionManager, address _ctf) {
        usdcTokenAddress = _usdcTokenAddress;
        usdc = IERC20(_usdcTokenAddress);
        vaultCreationTime = block.timestamp; // 记录合约创建的时间
        lastUpdateTimestamp = block.timestamp; // 初始化更新时间戳
        NonfungiblePositionManager = _NonfungiblePositionManager;
        ctfAddress = _ctf;
    }

    // 存入USDC并开始生息
    function deposit(uint256 amount) external {
        require(amount > 0, "Amount must be greater than 0");

        Deposit storage userDeposit = deposits[msg.sender];

        // 如果用户已经有存款了，先计算并更新利息
        if (userDeposit.principal > 0) {
            _accrueInterest(msg.sender);
        }

        // 从用户账户转移USDC
        usdc.transferFrom(msg.sender, address(this), amount);

        // 如果是首次存款，初始化 lastDepositTime
        if (userDeposit.principal == 0) {
            userDeposit.lastDepositTime = block.timestamp;
        }

        // 更新用户存款信息
        userDeposit.principal += amount;

        // 更新总本金
        totalPrincipal += amount;

        // 更新总利息
        _updateTotalInterest();
    }

    // 计算并更新总利息
    function _updateTotalInterest() internal {
        // 如果是第一次存款，不计算利息
        if (lastUpdateTimestamp == vaultCreationTime) {
            lastUpdateTimestamp = block.timestamp; // 更新最后更新时间
            return;
        }

        uint256 timeElapsed = block.timestamp - lastUpdateTimestamp; // 计算从上次更新时间到当前时间的差值
        
        // 利用秒数计算总利息
        uint256 interestPerSecond = totalPrincipal * DAILY_INTEREST_RATE / 1000 / SECONDS_IN_A_DAY; // 每秒的利息
        uint256 accruedInterest = interestPerSecond * timeElapsed; // 基于秒数计算的利息

        totalInterest += accruedInterest;
        lastUpdateTimestamp = block.timestamp; // 更新上次更新时间
    }

    // 计算并更新用户的利息
    function _accrueInterest(address user) internal {
        Deposit storage userDeposit = deposits[user];
        uint256 timeElapsed = block.timestamp - userDeposit.lastDepositTime;
        
        // 计算利息
        uint256 interestPerSecond = userDeposit.principal * DAILY_INTEREST_RATE / 1000 / SECONDS_IN_A_DAY; // 每秒的利息
        uint256 accruedInterest = interestPerSecond * timeElapsed; // 基于秒数计算的利息

        userDeposit.interestAccrued += accruedInterest;

        // 更新最后一次存款时间
        userDeposit.lastDepositTime = block.timestamp;
    }

    // 提取用户的本金和利息
    function withdraw() external {
        Deposit storage userDeposit = deposits[msg.sender];

        // 计算并更新利息
        _accrueInterest(msg.sender);

        uint256 totalAmount = userDeposit.principal + userDeposit.interestAccrued;
        require(totalAmount > 0, "No funds to withdraw");

        // 清除用户存款信息
        totalPrincipal -= userDeposit.principal;
        totalInterest -= userDeposit.interestAccrued;

        // 更新用户存款
        userDeposit.principal = 0;
        userDeposit.interestAccrued = 0;

        // 转账USDC给用户
        usdc.transfer(msg.sender, totalAmount);
    }

    // 查询用户当前的本金和利息
    function getBalance(address user) external view returns (uint256 principal, uint256 interest) {
        Deposit storage userDeposit = deposits[user];
        uint256 timeElapsed = block.timestamp - userDeposit.lastDepositTime;
        
        uint256 interestPerSecond = userDeposit.principal * DAILY_INTEREST_RATE / 1000 / SECONDS_IN_A_DAY; // 每秒的利息
        uint256 accruedInterest = interestPerSecond * timeElapsed; // 基于秒数计算的利息

        return (userDeposit.principal, userDeposit.interestAccrued + accruedInterest);
    }

    // 获取合约中当前总本金和总利息
    function getTotalBalance() external view returns (uint256 totalPrincipalAmount, uint256 totalInterestAmount) {
        uint256 timeElapsed = block.timestamp - lastUpdateTimestamp; // 计算自上次更新以来的时间
        
        uint256 interestPerSecond = totalPrincipal * DAILY_INTEREST_RATE / 1000 / SECONDS_IN_A_DAY; // 每秒的利息
        uint256 accruedInterest = interestPerSecond * timeElapsed; // 基于秒数计算的利息

        return (totalPrincipal, totalInterest + accruedInterest);
    }
    function addLiquidity(INonfungiblePositionManager.MintParams calldata params,
                          IERC20 collateralToken,
                          bytes32 parentCollectionId,
                          bytes32 conditionId,
                          uint[] calldata partition,
                          uint amount)
        external
        checkDeadline(params.deadline)
        returns (
            uint256 tokenId,
            uint128 liquidity,
            uint256 amount0,
            uint256 amount1
        )
    {
        //call ctf contract for wrapped 1155 
        CTF(ctfAddress).splitPosition(collateralToken, parentCollectionId, conditionId, partition, amount);
        (tokenId, liquidity,amount0, amount1) = INonfungiblePositionManager(NonfungiblePositionManager).mint(params); 
        //update occupied principal ,include erc1155 mint && usdb
        
    }
    function decreaseLiquidity(INonfungiblePositionManager.DecreaseLiquidityParams calldata params)
        external
        checkDeadline(params.deadline)
    {
        INonfungiblePositionManager(NonfungiblePositionManager).decreaseLiquidity(params);
        //update occupied principal
    }
    function onERC1155Received(
        address operator,
        address /* from */,
        uint256 id,
        uint256 value,
        bytes calldata data
    )
        external
        override
        returns (bytes4)
    {
        return this.onERC1155Received.selector;
    }
    function onERC1155BatchReceived(
        address operator,
        address /* from */,
        uint256[] calldata ids,
        uint256[] calldata values,
        bytes calldata data
    )
        external
        override
        returns (bytes4)
    {      
        return this.onERC1155BatchReceived.selector;
    }
}
